$(document).ready(function () {
    $("#userLogin").keyup(function () {
        $(this).val($(this).val().toUpperCase());
    });
}),
    $(document).ready(function () {
        $("#formLogin").validate({
            rules: { userLogin: { required: !0, lettersonly: !0 }, passwordLogin: { required: !0 } },
            messages: { userLogin: { required: "Digite o Login", lettersonly: "Digite apenas letras" }, passwordLogin: { required: "Digite a Senha" } },
            errorElement: "span",
            errorPlacement: function (a, b) {
                a.addClass("invalid-feedback"), b.closest(".form-group").append(a);
            },
            highlight: function (a) {
                $(a).addClass("is-invalid");
            },
            unhighlight: function (a) {
                $(a).removeClass("is-invalid");
            },
            submitHandler: function (a) {
                var b = $(a).serialize();
                return (
                    $.ajax({
                        type: "POST",
                        url: "/verificar-login",
                        data: b,
                        processData: !1,
                        success: function (a) {
                            if ("/home" === a.redirect && !1 === a.attempts && !1 === a.errors) window.location.href = "/carregar";
                            else if (!0 === a.errorStatus) toastr.warning("Usu\xE1rio inativo, entre em contato com Administrador do Sistema!");
                            else if (!0 === a.errors && !1 === a.attempts && "/home" === a.redirect) {
                                var b = a.opportunities + 1;
                                toastr.error("Erro: usu\xE1rio ou senha inv\xE1lidos, " + b + " tentativa(s) de 6!");
                            } else if (!0 === a.attempts && !0 === a.errors && "/home" === a.redirect && !1 === a.opportunities) {
                                toastr.error("Erro: usu\xE1rio ou senha inv\xE1lidos, 6 tentativa(s) de 6!");
                                window.location.href = "/"
                            }
                        },
                    }),
                    !1
                );
            },
        });
    });
