const cep=document.querySelector("#cep"),showData=a=>{for(const b in a)document.querySelector("#"+b)&&(document.querySelector("#"+b).value=a[b])};cep.addEventListener("blur",()=>{let a=cep.value.replace("-","");fetch(`https://viacep.com.br/ws/${a}/json/`,{method:"GET",modo:"cors",cache:"default"}).then(a=>{a.json().then(a=>showData(a))}).catch(a=>alert("CEP n\xE3o encontrado."+a))});